function [res] = find_distance(input,weight,output,ln)   [row,col] = size(input);   inc = 1;   w = 0;   ind = zeros(row,1);   val = zeros(row,1);   while inc <= row %counts upto 100     [w,val(inc,1), ind(inc,1)] = nn(input(inc,:),weight,output,ln);     inc = inc + 1;   end   inc = 1;    disp('Displaying the distance value and smallest distance location');  disp('    Index    Value');  while inc <= row %counts upto 100     fprintf('i%d    %d     %f\n',inc, ind(inc,1), val(inc,1));     inc = inc + 1;  endend 